mapper.select:

SELECT BNO, TITLE, CONTENT, CEOID, CEONAME, SHOPNAME, BAKERYPHOTO, REGDATE, BREADTYPE, BREADNAME, BREADPRICE
FROM
(
    /*알리아스를 붙인 컬럼에 WHERE절에서 접근할 때에는 FROM절에 작성된 테이블의 컬럼과 동일한 이름으로만 사용이 가능하다.*/
    SELECT /*+ INDEX_DESC(TBL_BOARD PK_BOARD) */
    ROWNUM R, BNO, TITLE, CONTENT, CEOID, CEONAME, SHOPNAME, BAKERYPHOTO, REGDATE, BREADTYPE, BREADNAME, BREADPRICE
    FROM TBL_BAKERY
    WHERE



ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}

SELECT BNO, TITLE, CONTENT, CEOID, CEONAME, SHOPNAME, BAKERYPHOTO, REGDATE, BREADTYPE, BREADNAME, BREADPRICE
FROM TBL_BAKERY
WHERE BNO = #{bno}

mapper.update:
UPDATE TBL_BAKERY
SET TITLE = #{title}, CONTENT = #{content}
WHERE BNO = #{bno}

UPDATE TBL_CEO
SET CEOPW = #{ceoPw}, SHOPNAME = #{shopName}, SHOPCALLNUMBER = #{shopCallNumber}, SHOPZIPCODE = #{shopZipcode}, SHOPADDRESS = #{shopAddress}, SHOPADDRESSDETAIL = #{shopAddressDetail}, SHOPPHOTO = '수정된 가게 이미지', PHONENUM = #{phoneNum}, SHOPREGNUM = #{shopRegNum}
WHERE CEOID = #{ceoId}

UPDATE TBL_CEO
SET STATUS = 1
WHERE CEOID = #{ceoId}

mapper.select:

SELECT STAR, REPLY, USERID, REPLYDATE
FROM
(
    SELECT /*+ INDEX_DESC(TBL_REPLY PK_REPLY) */
    ROWNUM R, RNO, BNO, STAR, REPLY, USERID, REPLYDATE
    FROM TBL_BAKERYREVIEWREPLY
    WHERE BNO = #{bno}
    AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
)
WHERE R > (#{criteria.pageNum} - 1) * #{criteria.amount}
