mapper.select:

SELECT BNO, TITLE, CONTENT, CEOID, CEONAME, BAKERYNAME, BAKERYZIPCODE, BAKERYADDRESS,
       BAKERYADDRESSDETAIL, BAKERYPHOTO, REGDATE
FROM
(
    /*알리아스를 붙인 컬럼에 WHERE절에서 접근할 때에는 FROM절에 작성된 테이블의 컬럼과 동일한 이름으로만 사용이 가능하다.*/
    SELECT /*+ INDEX_DESC(TBL_BOARD PK_BOARD) */
    ROWNUM R, BNO, TITLE, CONTENT, CEOID, CEONAME, BAKERYNAME, BAKERYZIPCODE, BAKERYADDRESS,
       BAKERYADDRESSDETAIL, BAKERYPHOTO, REGDATE
    FROM TBL_BAKERY
    WHERE



ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}

mapper.insert:
/*태그 안에서는 이와 같은 주석으로 사용해야 한다.*/
/*게시글 등록 전 다음 시퀀스를 가져와서 insertSelectKey_bno메소드의 매개변수로 매핑후 전달한다.*/

    SELECT TBL_BAKERY_SEQ.NEXTVAL FROM DUAL

/*위에서 전달받은 bno를 사용한다.*/
INSERT INTO TBL_BAKERY (BNO, TITLE, CONTENT, CEOID, CEONAME, BAKERYNAME, BAKERYZIPCODE, BAKERYADDRESS,
BAKERYADDRESSDETAIL, BAKERYPHOTO, REGDATE)
VALUES(#{bno}, #{title}, #{content}, #{ceoId}, #{ceoName}, #{bakeryName},
#{bakeryZipcode}, #{bakeryAddress}, #{bakeryAddressDetail}, #{bakeryPhoto}, #{regDate})

mapper.update:
UPDATE TBL_BAKERY
SET TITLE = #{title}, CONTENT = #{content}
WHERE BNO = #{bno}
