mapper.select:

SELECT BNO, TITLE, CEOID, CLASSPHOTO, CLASSPRICE, REGDATE, ENDDATE, CLASSDATE, RECRUITMENT, DESCRIPTION, BREADTYPE, BREADNAME, BREADPRICE, CEONAME
FROM
(
    SELECT
    ROWNUM R, BNO, TITLE, CEOID, CLASSPHOTO, CLASSPRICE, REGDATE, ENDDATE, CLASSDATE, RECRUITMENT, DESCRIPTION, BREADTYPE, BREADNAME, BREADPRICE, CEONAME
    FROM TBL_CEO_CLASS
    WHERE



ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}

SELECT BNO, TITLE, CEOID, CLASSPHOTO, CLASSPRICE, REGDATE, ENDDATE, CLASSDATE, RECRUITMENT, DESCRIPTION, BREADTYPE, BREADNAME, BREADPRICE, CEONAME
FROM TBL_CEO_CLASS
WHERE BNO = #{bno}

mapper.update:
UPDATE TBL_CEO_CLASS
SET TITLE = #{title}, CONTENT = #{content}
WHERE BNO = #{bno}
