mapper.select:

SELECT RNO, BNO, CONTENT, USERID, REVIEWDATE, STAR
FROM
(
    SELECT /*+ INDEX_DESC(TBL_REPLY PK_REPLY) */
    ROWNUM R, RNO, BNO, CONTENT, USERID, REVIEWDATE, STAR
    FROM TBL_CLASSREVIEWREPLY
    WHERE BNO = #{bno}
    AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
)
WHERE R > (#{criteria.pageNum} - 1) * #{criteria.amount}

SELECT RNO, BNO, CONTENT, REVIEWDATE, STAR
FROM TBL_CART WHERE USERID = #{userId}
(
SELECT /*+ INDEX_DESC(TBL_REPLY PK_REPLY) */
ROWNUM R, RNO, BNO, CONTENT, REVIEWDATE, STAR
FROM TBL_CLASSREVIEWREPLY
WHERE BNO = #{bno}
AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
)
WHERE R > (#{criteria.pageNum} - 1) * #{criteria.amount}
