mapper.update:
UPDATE TBL_TESTING_REQUEST
SET STATUS = 1
WHERE TNO = #{tno}

UPDATE TBL_TESTING_REQUEST
SET WINNINGDATAE= SYSDATE ,STATUS = 1
WHERE TNO = #{tno} AND USERID = #{userId}

UPDATE TBL_TESTING_REQUEST
SET WINNINGDATAE= SYSDATE ,STATUS = 1
WHERE TNO = #{tno} AND USERID = #{userId}

UPDATE TBL_TESTING_REQUEST
SET STATUS = -1
WHERE TNO = #{tno} AND USERID = #{userId}

mapper.delete:
UPDATE TBL_TESTING
SET STATUS = -1
WHERE TNO = #{tno}
