mapper.select:

SELECT TNO, STARTDATE, ENDDATE, CEOID, TITLE, BREADTYPE, BREADNAME, PEOPLE, DESCRIPTION, TESTINGPHOTO, SHOPNAME, REGDATE
FROM
(
    SELECT /*+ INDEX_DESC(TBL_BOARD PK_BOARD) */
    ROWNUM R, TNO, STARTDATE, ENDDATE, CEOID, TITLE, BREADTYPE, BREADNAME, PEOPLE, DESCRIPTION, TESTINGPHOTO, SHOPNAME, REGDATE
    FROM TBL_TESTING
    WHERE



ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}

SELECT TNO, STARTDATE, ENDDATE, CEOID, TITLE, BREADTYPE, BREADNAME, PEOPLE, DESCRIPTION, TESTINGPHOTO, SHOPNAME, REGDATE
FROM TBL_TESTING
WHERE TNO = #{tno}

mapper.update:
UPDATE TBL_TESTING
SET TITLE = #{title}, DESCRIPTION = #{description}
WHERE TNO = #{tno}
