mapper.update:
UPDATE TBL_USER
SET USERPW= #{userPw}
WHERE USERID= #{userId}

mapper.select:
SELECT USERID, USERPW, USERNAME, USERZIPCODE, USERADDRESS, USERADDRESS_DETAIL, USERPHONENUM, STATUS
FROM TBL_USER
WHERE USERID= #{userId}


    SELECT BNO, TITLE, CONTENT, CEOID, CEONAME, SHOPNAME, BAKERYPHOTO, REGDATE, BREADTYPE, BREADNAME, BREADPRICE FROM
    (
        SELECT ROWNUM R, BNO, TITLE, CONTENT, CEOID, CEONAME, SHOPNAME, BAKERYPHOTO, REGDATE, BREADTYPE, BREADNAME, BREADPRICE
        FROM TBL_BAKERY
        ORDER BY BNO DESC
    )
    WHERE



ROWNUM <= #{pageNum} * #{amount}
)
WHERE R > (#{pageNum} - 1) * #{amount}
